import React, { useEffect, useState } from "react";
import "./App.css";

// Components
import Navigation from "./components/navigation/Navigation";
import Project from "./components/project/Project";
import TopNav from "./components/navigation/TopNav";
import ProjectNav from "./components/navigation/ProjectNav";
import ProjectHead from "./components/project/ProjectHead";
import ProjectTitle from "./components/project/ProjectTitle";
import InputTask from "./components/project/InputTask";
import Tasklist from "./components/project/Tasklist";
import Task from "./components/project/Task";
import Important from "./components/project/Important";
import Completed from "./components/project/Completed";

// Navigation items
const navItems = ["All", "Important", "Today", "This Week", "Completed"];

// Principle function
function App() {
  const [tasks, setTasks] = useState([]); // Array of task objects
  const [projects, setProjects] = useState([]); // Array of projects
  const [activeProject, setActiveProject] = useState("Tasklist"); // Current active project
  const [isStar, setIsStar] = useState(false);

  // Set active project when main nav items clicked
  const handleTopNavClick = (e) => {
    setActiveProject(e.target.textContent);
  };

  // 'Add Project' function
  const handleAddProject = (e) => {
    const newProject = document.querySelector(".project-nav-item");
    setProjects([...projects, newProject]);
  };

  // When user clicks on project title, they are (or aren't) able to edit the project title via "invisible" input box
  const handleProjectEdit = (e) => {
    const projectTitleInput = document.querySelector(".edit-project-title");
    const projectTitle = document.querySelector(".project-title");
    if (
      activeProject === "All" ||
      activeProject === "Important" ||
      activeProject === "Completed" ||
      activeProject === "Today" ||
      activeProject === "This Week"
    ) {
      alert("Cannot change titles of categories that are not user-created.");
    } else {
      projectTitle.style.display = "none";
      projectTitleInput.style.display = "block";
      projectTitleInput.value = projectTitle.textContent;
    }
  };

  const handleProjectInput = (e) => {
    const projectTitleInput = document.querySelector(".edit-project-title");
    const projectTitle = document.querySelector(".project-title");
    const projectNavItems = document.querySelectorAll(".project-nav-item");

    if (e.key === "Enter") {
      projectTitle.textContent = projectTitleInput.value;
      projectTitleInput.style.display = "none";
      projectTitle.style.display = "block";
      projectNavItems.forEach((item) => {
        if (item.textContent === activeProject) {
          item.textContent = projectTitleInput.value;
        }
      });
      tasks.forEach((task) => {
        if (task.project === activeProject) {
          task.project = projectTitleInput.value;
        }
      });
      setActiveProject(projectTitleInput.value);
      console.log(tasks);
    }
  };

  const handleProjectTitle = (e) => {
    if (e.key === "Enter") {
      if (e.target.value === "" || e.target.value === " ") {
        alert(
          "ERROR: Project title cannot be blank. Please name your new project - you can always rename it later!"
        );
      } else {
        e.target.style.display = "none";
        e.target.parentElement.children[1].style.display = "block";
        e.target.parentElement.children[1].textContent = e.target.value;
        setActiveProject(e.target.value);
      }
    }
  };

  const handleProjectClick = (e) => {
    setActiveProject(e.target.textContent);
  };

  const handleAddTask = () => {
    const inputTask = document.querySelector(".type-task");
    const newTask = {
      id: tasks.length,
      title: inputTask.value,
      description: "",
      duedates: "",
      project: activeProject,
      important: false,
      completed: false,
    };
    setTasks([...tasks, newTask]);
    console.log(newTask);
    inputTask.focus();
    inputTask.value = "";
  };

  const handleImportance = (id) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === id) {
        return { ...task, important: !task.important };
      }
      setIsStar(!isStar);
      return task;
    });
    setTasks(updatedTasks);
    console.log(updatedTasks);
  };

  const handleCompleted = (id) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === id) {
        return { ...task, completed: !task.completed };
      }
      return task;
    });
    setTasks(updatedTasks);
    console.log(updatedTasks);
  };

  return (
    <div className="app">
      <Navigation>
        <TopNav>
          {navItems.map((item) => {
            return (
              <li className="nav-item" onClick={handleTopNavClick}>
                {item}
              </li>
            );
          })}
        </TopNav>
        <ProjectNav>
          {projects.map((project) => {
            return (
              <li className="project-nav-item" onClick={handleProjectClick}>
                <input
                  type="text"
                  className="type-project"
                  placeholder="i.e. Summer Chores"
                  onKeyPress={handleProjectTitle}
                />
                <span className="set-project"></span>
              </li>
            );
          })}
        </ProjectNav>
        <button className="add-project" onClick={handleAddProject}>
          Add Project
        </button>
      </Navigation>
      <Project>
        <ProjectHead>
          <ProjectTitle>
            <h1 className="project-title" onClick={handleProjectEdit}>
              {activeProject}
            </h1>
            <input
              onKeyPress={handleProjectInput}
              type="text"
              className="edit-project-title"
            />
          </ProjectTitle>
          {activeProject === "All" ||
          activeProject === "Important" ||
          activeProject === "Completed" ||
          activeProject === "Today" ||
          activeProject === "This Week" ? (
            <InputTask>
              <input
                type="text"
                className="type-task"
                disabled
                placeholder="Cannot add task right now."
              />
              <button className="add-task" onClick={handleAddTask}>
                Add Task
              </button>
            </InputTask>
          ) : (
            <InputTask>
              <input type="text" className="type-task" />
              <button className="add-task" onClick={handleAddTask}>
                Add Task
              </button>
            </InputTask>
          )}
        </ProjectHead>
        <Tasklist>
          {tasks.map((task) => {
            if (activeProject === "Important") {
              if (task.important === true) {
                return (
                  <Task
                    task={task}
                    tasks={tasks}
                    id={task.id}
                    title={task.title}
                    description={task.description}
                    dueDate={task.duedate}
                    completed={task.completed}
                    important={task.important}
                  >
                    <Completed onClick={handleCompleted} />
                    <Important
                      id={task.id}
                      onClick={() => handleImportance(task.id)}
                      isStar={isStar}
                    />
                  </Task>
                );
              }
            } else if (activeProject === "Completed") {
              if (task.completed === true) {
                <Task
                  task={task}
                  tasks={tasks}
                  id={task.id}
                  title={task.title}
                  description={task.description}
                  dueDate={task.duedate}
                  completed={task.completed}
                  important={task.important}
                >
                  <Important
                    id={task.id}
                    onClick={() => handleImportance(task.id)}
                    isStar={isStar}
                  />
                </Task>;
              }
            } else if (activeProject === "All") {
              return (
                <Task
                  task={task}
                  tasks={tasks}
                  id={task.id}
                  title={task.title}
                  description={task.description}
                  dueDate={task.duedate}
                  completed={task.completed}
                  important={task.important}
                >
                  <Completed onChange={handleCompleted} />
                  <Important
                    id={task.id}
                    onClick={() => handleImportance(task.id)}
                    isStar={isStar}
                  />
                </Task>
              );
            } else {
              if (task.project === activeProject) {
                return (
                  <Task
                    title={task.title}
                    description={task.description}
                    dueDate={task.duedate}
                    important={task.important}
                    completed={task.completed}
                    id={task.id}
                  >
                    <Completed onChange={handleCompleted} />

                    <Important
                      id={task.id}
                      onClick={() => handleImportance(task.id)}
                      isStar={isStar}
                    />
                  </Task>
                );
              }
            }
          })}
        </Tasklist>
      </Project>
    </div>
  );
}

export default App;
