  });

  return (
    <>
      <div className="modal-bg" onClick={() => setIsOpen(false)} />
      <div className="centered">
        <div className="modal">
          <div className="modal-header">
            <h5 className="modal-heading">Edit Task</h5>
            <button className="modal-close" onClick={() => setIsOpen(false)}>
              <i class="fa-solid fa-xmark"></i>
            </button>
          </div>
          <div className="modal-content">
            <label for="modal-title">Title:</label>
            <input
              type="text"
              placeholder="Ex: Do laundry"
              className="modal-title-input"
            />
            <label for="modal-description">Description:</label>
            <textarea className="modal-description-input" />
            <label for="modal-duedate" className="modal-duedate-input">
              Due Date:
            </label>
            <input type="date" />
          </div>
          <div className="modal-footer">
            <div className="modal-actions">
              <button className="modal-confirm">Submit</button>
              <button className="modal-cancel">Cancel</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
