import React, { useState } from "react";
import "./App.css";

import Navigation from "./components/navigation/Navigation";
import Project from "./components/project/Project";
import TopNav from "./components/navigation/TopNav";
import ProjectNav from "./components/navigation/ProjectNav";
import ProjectHead from "./components/project/ProjectHead";
import ProjectTitle from "./components/project/ProjectTitle";
import InputTask from "./components/project/InputTask";
import TaskList from "./components/project/TaskList";
import ProjectNavItem from "./components/navigation/ProjectNavItem";

function App() {
  const [activeProject, setActiveProject] = useState("Project");
  const [projects, setProjects] = useState([]);

  const handleAddProject = () => {
    const newProject = document.querySelector(".new-project");
    setProjects([...projects, newProject]);
  };

  const handleProjectInput = (e) => {
    if (e.key === "Enter") {
      if (e.target.value === "" || e.target.value === " ") {
        alert("ERROR: Project title must not be blank.");
      } else {
        e.target.style.display = "none";
        e.target.parentElement.children[0].style.display = "block";
        e.target.parentElement.children[0].textContent = e.target.value;
        setActiveProject(e.target.value);
      }
    }
  };

  const handleAddTask = () => {
    const task = document.querySelector(".task-input");
    console.log(task.value);
    task.value = "";
    task.focus();
  };

  return (
    <div className="App">
      <Navigation>
        <TopNav />
        <ProjectNav>
          ProjectNav
          {projects.map((item, i) => (
            <ProjectNavItem className="new-project" key={i}>
              <span className="set-project">{item}</span>
              <input
                type="text"
                className="type-project"
                placeholder="Type project name..."
                onKeyPress={handleProjectInput}
              />
            </ProjectNavItem>
          ))}
          <button className="add-project" onClick={handleAddProject}>
            Add Project
          </button>
        </ProjectNav>
      </Navigation>
      <Project>
        <ProjectHead>
          <ProjectTitle title={activeProject} />
          <InputTask>
            <input
              className="task-input"
              type="text"
              placeholder="Enter task"
            />
            <button className="submit-task" onClick={handleAddTask}>
              Submit
            </button>
          </InputTask>
        </ProjectHead>
        <TaskList />
      </Project>
    </div>
  );
}

export default App;
