import React from "react";

export default function Task(props) {
  const handleImportantToggle = (e) => {
    if (e.target.className === "fa-solid fa-star star") {
      e.target.className = "fa-solid fa-star no-star";
    } else {
      e.target.className = "fa-solid fa-star star";
      alert("Sure");
    }
  };

  return (
    <li className="task">
      <i className="fa-solid fa-star star" onClick={handleImportantToggle}></i>;
      {props.title}
    </li>
  );
}
