import React, { useState } from "react";
import "./App.css";

import Navigation from "./components/navigation/Navigation";
import Project from "./components/project/Project";
import TopNav from "./components/navigation/TopNav";
import ProjectNav from "./components/navigation/ProjectNav";
import ProjectHead from "./components/project/ProjectHead";
import ProjectTitle from "./components/project/ProjectTitle";
import InputTask from "./components/project/InputTask";
import Tasklist from "./components/project/Tasklist";
import Task from "./components/project/Task";

function App() {
  const [navItems, setNavItems] = useState([
    "All",
    "Important",
    "Today,",
    "Next Week",
    "Completed",
  ]);
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [activeProject, setActiveProject] = useState("Tasklist");

  const handleTopNavClick = (e) => {
    setActiveProject(e.target.textContent);
  };

  const handleAddProject = (e) => {
    const newProject = document.querySelector(".project-nav-item");
    setProjects([...projects, newProject]);
  };

  const handleProjectTitle = (e) => {
    if (e.key === "Enter") {
      if (e.target.value === "" || e.target.value === " ") {
        alert(
          "ERROR: Project title cannot be blank. Please name your new project - you can always rename it later!"
        );
      } else {
        e.target.style.display = "none";
        e.target.parentElement.children[1].style.display = "block";
        e.target.parentElement.children[1].textContent = e.target.value;
        setActiveProject(e.target.value);
      }
    }
  };

  const handleProjectClick = (e) => {
    setActiveProject(e.target.textContent);
  };

  const handleAddTask = () => {
    const inputTask = document.querySelector(".type-task");
    const newTask = {
      id: tasks.length,
      title: inputTask.value,
      description: "",
      duedates: "",
      project: activeProject,
      important: false,
      completed: false,
    };
    setTasks([...tasks, newTask]);
    console.log(newTask);
    inputTask.focus();
    inputTask.value = "";
  };

  const handleTask = (task) => {
    if (task.important === false) {
      alert("Yes");
    } else {
      alert("No");
    }
  };

  return (
    <div className="app">
      <Navigation>
        <TopNav>
          {navItems.map((item) => {
            return (
              <li className="nav-item" onClick={handleTopNavClick}>
                {item}
              </li>
            );
          })}
        </TopNav>
        <ProjectNav>
          {projects.map((project) => {
            return (
              <li className="project-nav-item" onClick={handleProjectClick}>
                <input
                  type="text"
                  className="type-project"
                  placeholder="i.e. Summer Chores"
                  onKeyPress={handleProjectTitle}
                />
                <span className="set-project"></span>
              </li>
            );
          })}
        </ProjectNav>
        <button className="add-project" onClick={handleAddProject}>
          Add Project
        </button>
      </Navigation>
      <Project>
        <ProjectHead>
          <ProjectTitle title={activeProject} />
          <InputTask>
            <input type="text" className="type-task" />
            <button className="add-task" onClick={handleAddTask}>
              Add Task
            </button>
          </InputTask>
        </ProjectHead>
        <Tasklist>
          {tasks.map((task) => {
            if (activeProject === "All") {
              return (
                <Task
                  id={task.id}
                  title={task.title}
                  description={task.description}
                  dueDate={task.duedate}
                  completed={task.completed}
                  important={task.important}
                >
                  <button onClick={() => handleTask(task)}>
                    <i
                      className={
                        task.important
                          ? "fa-solid fa-star star"
                          : "fa-solid fa-star no-star"
                      }
                    ></i>
                  </button>
                </Task>
              );
            } else {
              if (task.project === activeProject) {
                return (
                  <Task
                    title={task.title}
                    description={task.description}
                    dueDate={task.duedate}
                    important={task.important}
                    completed={task.completed}
                  >
                    <button onClick={() => handleTask(task.important)}>
                      <i
                        className={
                          task.important
                            ? "fa-solid fa-star star"
                            : "fa-solid fa-star no-star"
                        }
                      ></i>
                    </button>
                  </Task>
                );
              }
            }
          })}
        </Tasklist>
      </Project>
    </div>
  );
}

export default App;
