import e from "express";
import React from "react";
import "./modal.css";

export default function Modal(props) {
  const modalTitle = document.querySelector(".modal-title-input");
  const modalDescription = document.querySelector(".modal-description-input");
  const modalDate = document.querySelector(".modal-duedate-input");

  const taskSpans = document.querySelectorAll(".task-title");

  taskSpans.forEach((task) => {
    if (task.id === "task-" + props.id) {
      alert(task.id);
    }
  });

  const handleClick = () => {
    props.tasks.map((task) => {
      if (task.id === props.id) {
        task.title = modalTitle.value;
        task.description = modalDescription.value;
        task.dueDate = modalDate.value;
        console.log(task);
      }
    });
  };

  return (
    <>
      <div className="modal-bg" onClick={() => props.setIsOpen(false)} />
      <div className="centered">
        <div className="modal">
          <div className="modal-header">
            <h5 className="modal-heading">Edit Task</h5>
            <button
              className="modal-close"
              onClick={() => props.setIsOpen(false)}
            >
              <i className="fa-solid fa-xmark"></i>
            </button>
          </div>
          <div className="modal-content">
            <label for="modal-title">Title:</label>
            <input
              type="text"
              placeholder="Ex: Do laundry"
              className="modal-title-input"
            />
            <label for="modal-description">Description:</label>
            <textarea className="modal-description-input">
              {props.tasks.map((task) =>
                task.id === props.id ? task.description : ""
              )}
            </textarea>
            <label for="modal-duedate" className="modal-duedate">
              Due Date:
            </label>
            <input type="date" className="modal-duedate-input" />
          </div>
          <div className="modal-footer">
            <div className="modal-actions">
              <button className="modal-confirm" onClick={handleClick}>
                Submit
              </button>
              <button className="modal-cancel">Cancel</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
